SELECT pnt.id_campana_aviso id_tpo, pnt.id_pnt, pnt.id,  ej.ejercicio, cam.fecha_inicio_periodo, cam.fecha_termino_periodo, cam.mensajeTO, /*Hiperv√≠nculo, */ cam.fecha_validacion, cam.fecha_actualizacion, cam.area_responsable, cam.nota FROM tab_campana_aviso cam JOIN cat_ejercicios ej ON ej.id_ejercicio = cam.id_ejercicio LEFT JOIN rel_pnt_campana_aviso pnt ON pnt.id_campana_aviso = pdes.id_campana_aviso;